        <div className= "AddingTaskBlock">
            <input id="textInput" className="inputTask" placeholder="Write your task"  label={"name"} onChange={(event)=>this.changeTaskValue(event)}/>
            <div className = "addButton" onClick={()=>this.addTask()}></div>
            <div className = "cancelButton" onClick ={()=>this.cancelButton()}></div>
            <div className = "startCalendar" onClick={()=> this.startCalendar()} ></div>
            {
  this.state.flag === false  ? console.log("loh"): 

  <div className=" Calendar">
    <div className ="changeButtonsBlock">
      <div className ="changeMonthDecrease" onClick ={() => this.changeMonthDecrease()}></div>
      <div  className="changeMonthAdd" onClick ={() => this.changeMonthAdd()}></div>
    </div>
       <table border = "3" cellpadding="3" >
       <tr>
        <th>
          {this.state.todayDate.getFullYear() +  "  " +this.state.monthName[this.state.todayDate.getMonth()]}</th>
               </tr>
           <div>

                <tr className = "Sunday">

                  <th>Sunday</th>
                  {this.state.daysOfWeekCurrent[0].map((element,i)=>{
                       console.log(i)
         return(

                    <td >{this.state.daysOfWeekCurrent[0][i]}</td>

                    )     


                  })

              }
              </tr>
              <tr className = "Monday">
                  <th>Monday</th>
            {this.state.daysOfWeekCurrent[1].map((element,i)=>{
         return(
          <td>{this.state.daysOfWeekCurrent[1][i]}</td>
          )                    
                  })
              }
        </tr>
        <tr className = "Tuesday">
        <th>Tuesday</th>
        {this.state.daysOfWeekCurrent[2].map((element,i)=>{

         return(

          <td>{this.state.daysOfWeekCurrent[2][i]}</td>

          )                    
        })
    }
        </tr>
        <tr className = "Wednesday">
        <th>Wednesday</th>

        {this.state.daysOfWeekCurrent[3].map((element,i)=>{

         return(

          <td>{this.state.daysOfWeekCurrent[3][i]}</td>

          )                    
        })
    }
        </tr>
        <tr className = "Thursday">
        <th>Thursday</th>
        {this.state.daysOfWeekCurrent[4].map((element,i)=>{
         return(
          <td>{this.state.daysOfWeekCurrent[4][i]}</td>
          )                    
        })
    }
        </tr>
        <tr className = "Friday">
        <th>Friday</th>
        {this.state.daysOfWeekCurrent[5].map((element,i)=>{
         return(
          <td>{this.state.daysOfWeekCurrent[5][i]}</td>
          )                    
        })
    }

        </tr>
        <tr className = "Saturday">
        <th>Saturday</th>
        {this.state.daysOfWeekCurrent[6].map((element,i)=>{
         return(
          <td>{this.state.daysOfWeekCurrent[6][i]}</td>
          )                    
        })
    }

        </tr>
           </div>

      </table> 


      </div>
  }
            </div>

</div> 
     </div>
  )}


