/* eslint-disable react/jsx-no-duplicate-props */
import React from 'react';
import './App.css';
class App extends  React.Component{

  state = {
    task: " ",
    tasks: [],
    dateTask: [],
    editedTask: "",
    flag: false,
    todayDate: new Date(),
    daysOfWeekCurrent: {},
    result: "",
    changeMonthFlag: 0,
    daysOfWeekI: "",
    monthName: ["January","February","March","April","May","June","July","August","September","October","November","December"],

  }
  callServer = async () => {
    console.log('calling server')
   await fetch('http://localhost:4000/api/getAllTasks', {
      method: "POST",
      mode: 'cors',
      headers: {
        'Content-Type': 'application/json'
        // 'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: JSON.stringify({
        "calendarTask": "First task",
        "calendarDate": "first may",
        "calendarBool": "true"

      })
    }
    )
  .then((response) => {
    console.log('response', response)
    return response.json();
  })
  .catch((err) => {
    console.log('error from server',err);
  });
  }

  componentWillMount() {
    console.log('calling server from frontend')
    this.callServer()

  }
  changeTaskValue = (event)=> {   
    this.setState({
        task: event.target.value,
    })
  }
  editTaskValue = (event)=> {   
    this.setState({
        editedTask: event.target.value
    })
  }
  doneTask = (i) =>{
    if(this.state.tasks[i].isChecked === 1){
    var currentTask = this.state.tasks
    currentTask[i].isChecked = 2
    }
    else{
    currentTask = this.state.tasks
    currentTask[i].isChecked = 1
    }
     this.setState({
       tasks: currentTask,

     })

  }
  editButton =(i,element) =>{    
     if(this.state.tasks[i].isChecked === 1){
       var currentEdit = this.state.tasks
       currentEdit[i].isChecked = 3

     }
     else{
      currentEdit = this.state.tasks
      currentEdit[i].isChecked = 1
     }
