/* eslint-disable react/jsx-no-duplicate-props */
import React from 'react';
import './App.css';
class App extends  React.Component{

  state = {
    task: " ",
    tasks: [],
    dateTask: [],
    editedTask: "",
    flag: false,
    todayDate: new Date(),
    daysOfWeekCurrent: {},
    result: "",
    changeMonthFlag: 0,
    daysOfWeekI: "",
    taskId:0,
    monthName: ["January","February","March","April","May","June","July","August","September","October","November","December"],

  }
  callServer = async () => {
    console.log('calling server')

   const linkToServer = await fetch('http://localhost:8080/api/getAllTasks')
  .then( async(response) => {
    const resp = await response.json()
    return resp;
  })
  .catch((err) => {
    console.log('error from server',err);
  });
  this.setState({
      tasks: linkToServer
    })
  }

  componentWillMount() {
    console.log('calling server from frontend')
    this.callServer()

  }
  changeTaskValue = (event)=> {   
    this.setState({
        task: event.target.value,
    })
  }
  editTaskValue = (event)=> {   
    this.setState({
        editedTask: event.target.value
    })
  }
  doneTask = (i) =>{
    console.log(i.done)
